import { login, logout } from "./types";

const initialState = {
    token: null,
    userAuth: null,
    error: null
}

const reducer = (state = initialState, action) => {
    if (action.type === login) {
        return {
            ...state,
            token: action.payload,
            userAuth: true,
        }
    }
    else if (action.type === logout) {
        return {
            ...state,
            token: null,
            userAuth: null,
            error: null
        }
    }
    return state;
}

export default reducer;
