import React, { useState } from 'react';
import { connect } from 'react-redux';
import { loginFunction } from "../../store/actions/loginAction";


const Login = (props) => {

    const [email, setEmail] = useState('sagi@email.com');
    const [password, setPassword] = useState('propulsion');

    const userLoginHandler = (e) => {
        e.preventDefault();
        const data = {
            email,
            password
        };
        props.dispatch(loginFunction(data))
    };

    return <>
        This is the login page.
        <form>
            <input placeholder='sagi@email.com' onChange={(e) => setEmail(e.currentTarget.value)} /><br/>
            <input placeholder='propulsion' onChange={(e) => setPassword(e.currentTarget.value)} /><br/>
            <button type="submit" onClick={userLoginHandler} >Login</button>
        </form>
    </>
}

export default connect()(Login);
